/**
 * Process EverWebinar registrants and sync to Supabase
 * This combines processing and database sync in one node
 */

const response = $input.first().json;
console.log('ðŸ” Processing EverWebinar API Response');

// Access registrants.data from the API response
const registrantsData = response?.registrants?.data || [];

if (!Array.isArray(registrantsData) || registrantsData.length === 0) {
  console.log('âŒ No registrants found in response');
  return [{ json: { message: 'No registrants found', total: 0 } }];
}

console.log(`âœ… Found ${registrantsData.length} registrants to process`);

// Process each registrant
const processedRegistrants = [];

for (const reg of registrantsData) {
  // Parse registration date (signup_date)
  let registrationDate = null;
  if (reg.signup_date) {
    try {
      registrationDate = new Date(reg.signup_date).toISOString();
    } catch (e) {
      console.log('Date parse error for signup_date:', reg.signup_date);
    }
  }

  // Parse webinar event date
  let webinarDate = null;
  if (reg.event) {
    try {
      const eventDate = new Date(reg.event);
      if (!isNaN(eventDate.getTime())) {
        webinarDate = eventDate.toISOString();
      }
    } catch (e) {
      console.log('Event date parse error for:', reg.event);
    }
  }

  // Combine phone country code and number
  let fullPhone = '';
  if (reg.phone_country_code && reg.phone_number) {
    fullPhone = reg.phone_country_code + reg.phone_number;
  } else if (reg.phone_number) {
    fullPhone = reg.phone_number;
  }

  // Determine attendance status
  const attendedLive = reg.attended_live === 'Yes';
  const attendedReplay = reg.attended_replay === 'Yes';
  const attended = attendedLive || attendedReplay;

  const processedReg = {
    email: (reg.email || '').toLowerCase().trim(),
    first_name: reg.first_name || '',
    last_name: reg.last_name || '',
    phone: fullPhone,
    webinar_id: 13,
    schedule_id: parseInt(reg.schedule_id) || 46,
    registration_date: registrationDate,
    webinar_date: webinarDate,
    timezone: 'Europe/Amsterdam',
    live_room_url: reg.links?.live_room || '',
    replay_room_url: reg.links?.replay_room || '',
    thank_you_url: '',
    attended: attended,
    attended_live: attendedLive,
    attended_replay: attendedReplay,
    event_id: parseInt(reg.event_id) || null,
    lead_id: parseInt(reg.lead_id) || null,
    last_updated: new Date().toISOString()
  };

  processedRegistrants.push(processedReg);
}

console.log(`âœ… Processed ${processedRegistrants.length} registrants`);

// Return all processed registrants for the next node
return processedRegistrants.map(reg => ({ json: reg }));

/**
 * Summary of sync operation
 */

const results = $input.all();
const successful = results.filter(r => r.json && !r.json.error).length;
const errors = results.filter(r => r.json && r.json.error).length;

const apiResponse = $('Fetch EverWebinar Registrants').first().json;
const totalPages = apiResponse?.registrants?.last_page || 1;
const currentPage = apiResponse?.registrants?.current_page || 1;
const totalRegistrants = apiResponse?.registrants?.total || 0;

console.log(`ðŸ“Š SYNC COMPLETE:`);
console.log(`âœ… Successful: ${successful}`);
console.log(`âŒ Errors: ${errors}`);
console.log(`ðŸ“„ Page: ${currentPage}/${totalPages}`);
console.log(`ðŸ‘¥ Total Registrants: ${totalRegistrants}`);
console.log(`ðŸ“… Timestamp: ${new Date().toISOString()}`);

return [{
  json: {
    sync_completed: true,
    timestamp: new Date().toISOString(),
    successful_syncs: successful,
    errors: errors,
    total_processed: results.length,
    current_page: currentPage,
    total_pages: totalPages,
    total_registrants: totalRegistrants,
    needs_pagination: totalPages > 1
  }
}];
