// Parse WebinarJam API response and prepare for Supabase
const response = $input.first().json;

if (!response.status || response.status !== 'success') {
  throw new Error(`WebinarJam API Error: ${response.message || 'Unknown error'}`);
}

const registrants = response.registrants || [];
const totalPages = Math.ceil((response.total_items || 0) / (response.per_page || 100));

console.log(`Processing ${registrants.length} registrants from page 1 of ${totalPages}`);

// Transform each registrant for Supabase
const transformedRegistrants = registrants.map(registrant => {
  // Parse dates safely
  const parseDate = (dateStr) => {
    if (!dateStr || dateStr === '0000-00-00 00:00:00') return null;
    try {
      return new Date(dateStr).toISOString();
    } catch (e) {
      return null;
    }
  };

  // Parse intervals (like "01:23:45" to PostgreSQL INTERVAL)
  const parseInterval = (timeStr) => {
    if (!timeStr || timeStr === '00:00:00') return null;
    return timeStr; // PostgreSQL will handle HH:MM:SS format
  };

  return {
    webinarjam_id: parseInt(registrant.id) || 0,
    lead_id: parseInt(registrant.lead_id) || null,
    webinar_id: parseInt(registrant.webinar_id) || 0,
    schedule_id: parseInt(registrant.schedule_id) || 0,
    event_id: parseInt(registrant.event_id) || null,

    // Contact Info
    email: registrant.email || '',
    first_name: registrant.first_name || '',
    last_name: registrant.last_name || '',
    phone_country_code: registrant.phone_country_code || null,
    phone_number: registrant.phone_number || null,

    // Webinar Details
    webinar_name: registrant.webinar_name || '',
    schedule: registrant.schedule || '',
    event_date: parseDate(registrant.event_date),
    signup_date: parseDate(registrant.signup_date),

    // Attendance Status
    attended_live: registrant.attended_live === '1' || registrant.attended_live === true,
    date_live: parseDate(registrant.date_live),
    entered_live: parseInterval(registrant.entered_live),
    time_live: parseInterval(registrant.time_live),

    // Purchase Data
    purchased_live: registrant.purchased_live === '1' || registrant.purchased_live === true,
    revenue_live: parseFloat(registrant.revenue_live) || 0,

    // Replay Data
    attended_replay: registrant.attended_replay === '1' || registrant.attended_replay === true,
    date_replay: parseDate(registrant.date_replay),
    time_replay: parseInterval(registrant.time_replay),
    purchased_replay: registrant.purchased_replay === '1' || registrant.purchased_replay === true,
    revenue_replay: parseFloat(registrant.revenue_replay) || 0,

    // Subscription Status
    subscribed: registrant.subscribed !== '0' && registrant.subscribed !== false,
    gdpr_status: registrant.gdpr_status || null,
    gdpr_communications: registrant.gdpr_communications || null,

    // UTM Tracking
    utm_source: registrant.utm_source || null,
    utm_medium: registrant.utm_medium || null,
    utm_campaign: registrant.utm_campaign || null,
    utm_term: registrant.utm_term || null,
    utm_content: registrant.utm_content || null
  };
});

return {
  registrants: transformedRegistrants,
  pagination: {
    current_page: 1,
    total_pages: totalPages,
    total_items: response.total_items || 0,
    per_page: response.per_page || 100
  },
  sync_timestamp: new Date().toISOString()
};

// Create pagination loop for remaining pages
const pagination = $('transform-data').item.json.pagination;
const totalPages = pagination.total_pages;
const startPage = 2; // We already processed page 1

const pageRequests = [];

for (let page = startPage; page <= totalPages; page++) {
  pageRequests.push({
    page: page,
    total_pages: totalPages
  });
}

console.log(`Creating ${pageRequests.length} additional page requests (pages ${startPage}-${totalPages})`);

return pageRequests.map(req => ({ json: req }));

// Transform additional page data for Supabase
const response = $input.item.json;

if (!response.status || response.status !== 'success') {
  console.log(`Warning: Page ${$input.item.json.page || 'unknown'} failed: ${response.message || 'Unknown error'}`);
  return { json: { registrants: [], page: $input.item.json.page || 0 } };
}

const registrants = response.registrants || [];
const currentPage = response.current_page || $input.item.json.page || 0;

console.log(`Processing ${registrants.length} registrants from page ${currentPage}`);

// Transform each registrant for Supabase (same logic as main transform)
const transformedRegistrants = registrants.map(registrant => {
  const parseDate = (dateStr) => {
    if (!dateStr || dateStr === '0000-00-00 00:00:00') return null;
    try {
      return new Date(dateStr).toISOString();
    } catch (e) {
      return null;
    }
  };

  const parseInterval = (timeStr) => {
    if (!timeStr || timeStr === '00:00:00') return null;
    return timeStr;
  };

  return {
    webinarjam_id: parseInt(registrant.id) || 0,
    lead_id: parseInt(registrant.lead_id) || null,
    webinar_id: parseInt(registrant.webinar_id) || 0,
    schedule_id: parseInt(registrant.schedule_id) || 0,
    event_id: parseInt(registrant.event_id) || null,

    email: registrant.email || '',
    first_name: registrant.first_name || '',
    last_name: registrant.last_name || '',
    phone_country_code: registrant.phone_country_code || null,
    phone_number: registrant.phone_number || null,

    webinar_name: registrant.webinar_name || '',
    schedule: registrant.schedule || '',
    event_date: parseDate(registrant.event_date),
    signup_date: parseDate(registrant.signup_date),

    attended_live: registrant.attended_live === '1' || registrant.attended_live === true,
    date_live: parseDate(registrant.date_live),
    entered_live: parseInterval(registrant.entered_live),
    time_live: parseInterval(registrant.time_live),

    purchased_live: registrant.purchased_live === '1' || registrant.purchased_live === true,
    revenue_live: parseFloat(registrant.revenue_live) || 0,

    attended_replay: registrant.attended_replay === '1' || registrant.attended_replay === true,
    date_replay: parseDate(registrant.date_replay),
    time_replay: parseInterval(registrant.time_replay),
    purchased_replay: registrant.purchased_replay === '1' || registrant.purchased_replay === true,
    revenue_replay: parseFloat(registrant.revenue_replay) || 0,

    subscribed: registrant.subscribed !== '0' && registrant.subscribed !== false,
    gdpr_status: registrant.gdpr_status || null,
    gdpr_communications: registrant.gdpr_communications || null,

    utm_source: registrant.utm_source || null,
    utm_medium: registrant.utm_medium || null,
    utm_campaign: registrant.utm_campaign || null,
    utm_term: registrant.utm_term || null,
    utm_content: registrant.utm_content || null
  };
});

return {
  json: {
    registrants: transformedRegistrants,
    page: currentPage
  }
};

// Create comprehensive sync report
const syncSummary = $('sync-summary').first().json;
const initialSync = $('sync-to-supabase').first().json;
const additionalPagesResult = $('sync-additional-pages').all();

const report = {
  sync_completed_at: new Date().toISOString(),
  webinar_id: 13,
  total_registrants_synced: syncSummary.total_synced || 0,
  attended_live_count: syncSummary.attended_count || 0,
  no_show_count: syncSummary.no_show_count || 0,
  attendance_rate: syncSummary.total_synced > 0 ? 
    Math.round((syncSummary.attended_count / syncSummary.total_synced) * 100 * 100) / 100 : 0,
  webinar_name: syncSummary.webinar_name || 'Unknown Webinar',
  pages_processed: 1 + (additionalPagesResult?.length || 0),
  database_ready: true,
  next_sync_in: '6 hours'
};

console.log('ğŸ“Š SYNC COMPLETED');
console.log(`âœ… ${report.total_registrants_synced} registrants synced`);
console.log(`ğŸ‘¥ ${report.attended_live_count} attended live (${report.attendance_rate}%)`);
console.log(`âŒ ${report.no_show_count} no-shows`);
console.log(`ğŸ“„ ${report.pages_processed} pages processed`);
console.log(`ğŸ”„ Next sync in ${report.next_sync_in}`);

return { json: report };
